import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Register = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    password: '',
    confirmPassword: '',
    address: '',
    locality: '',
    pincode: '',
    communityCode: '',
    userRole: 'RESIDENT'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const validateForm = () => {
    if (!formData.fullName || !formData.password || !formData.confirmPassword) {
      setError('Please fill in all required fields');
      return false;
    }

    if (!formData.email && !formData.phoneNumber) {
      setError('Please provide either email or phone number');
      return false;
    }

    if (formData.password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!validateForm()) {
      setLoading(false);
      return;
    }

    // Prepare registration data
    const registrationData = {
      fullName: formData.fullName,
      password: formData.password,
      address: formData.address,
      locality: formData.locality,
      pincode: formData.pincode,
      communityCode: formData.communityCode,
      userRole: formData.userRole
    };

    // Add email or phone number
    if (formData.email) {
      registrationData.email = formData.email;
    }
    if (formData.phoneNumber) {
      registrationData.phoneNumber = formData.phoneNumber;
    }

    const result = await register(registrationData);
    
    if (result.success) {
      navigate('/login');
    } else {
      setError(result.error);
    }
    
    setLoading(false);
  };

  return (
    <div className="container">
      <div className="auth-container fade-in" style={{ maxWidth: '500px' }}>
        <div className="auth-header">
          <h2>üß± Join Tangle</h2>
          <p>Create your account and connect with your community</p>
        </div>

        {error && (
          <div style={{
            background: 'var(--error-color)',
            color: 'white',
            padding: '12px',
            borderRadius: 'var(--border-radius-sm)',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="fullName" className="form-label">
              Full Name *
            </label>
            <input
              type="text"
              id="fullName"
              name="fullName"
              className="form-input"
              value={formData.fullName}
              onChange={handleChange}
              placeholder="Enter your full name"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="email" className="form-label">
              Email Address
            </label>
            <input
              type="email"
              id="email"
              name="email"
              className="form-input"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email address"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="phoneNumber" className="form-label">
              Phone Number
            </label>
            <input
              type="tel"
              id="phoneNumber"
              name="phoneNumber"
              className="form-input"
              value={formData.phoneNumber}
              onChange={handleChange}
              placeholder="Enter your phone number"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password" className="form-label">
              Password *
            </label>
            <input
              type="password"
              id="password"
              name="password"
              className="form-input"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a strong password (min 8 characters)"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword" className="form-label">
              Confirm Password *
            </label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              className="form-input"
              value={formData.confirmPassword}
              onChange={handleChange}
              placeholder="Confirm your password"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="userRole" className="form-label">
              I am a...
            </label>
            <select
              id="userRole"
              name="userRole"
              className="form-select"
              value={formData.userRole}
              onChange={handleChange}
              disabled={loading}
            >
              <option value="RESIDENT">Resident</option>
              <option value="BUSINESS_OWNER">Business Owner</option>
              <option value="SERVICE_PROVIDER">Service Provider</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              id="address"
              name="address"
              className="form-input"
              value={formData.address}
              onChange={handleChange}
              placeholder="Enter your address"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="locality" className="form-label">
              Locality
            </label>
            <input
              type="text"
              id="locality"
              name="locality"
              className="form-input"
              value={formData.locality}
              onChange={handleChange}
              placeholder="Enter your locality/area"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="pincode" className="form-label">
              Pincode
            </label>
            <input
              type="text"
              id="pincode"
              name="pincode"
              className="form-input"
              value={formData.pincode}
              onChange={handleChange}
              placeholder="Enter your pincode"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="communityCode" className="form-label">
              Community Code (Optional)
            </label>
            <input
              type="text"
              id="communityCode"
              name="communityCode"
              className="form-input"
              value={formData.communityCode}
              onChange={handleChange}
              placeholder="Enter community code if you have one"
              disabled={loading}
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            disabled={loading}
            style={{ width: '100%' }}
          >
            {loading ? (
              <span>
                <div className="spinner" style={{ 
                  width: '20px', 
                  height: '20px', 
                  margin: '0 auto',
                  display: 'inline-block'
                }}></div>
                Creating Account...
              </span>
            ) : (
              'Create Account'
            )}
          </button>
        </form>

        <div className="auth-links">
          <p>
            Already have an account?{' '}
            <Link to="/login">Sign in here</Link>
          </p>
        </div>

        {/* Info Box */}
        <div style={{
          background: 'var(--bg-secondary)',
          padding: '20px',
          borderRadius: 'var(--border-radius-sm)',
          marginTop: '30px'
        }}>
          <h4 style={{ marginBottom: '10px', color: 'var(--text-primary)' }}>
            ‚ÑπÔ∏è About Your Community
          </h4>
          <p style={{ fontSize: '0.9rem', color: 'var(--text-secondary)', lineHeight: '1.5' }}>
            You can join a community using a community code, or we can help you find 
            communities in your area. Communities help organize posts and ensure 
            you connect with your actual neighbors.
          </p>
        </div>
      </div>
    </div>
  );
